export default function MessagesScreen({ navigation }) {
  const [loading, setLoading] = useState(true);
  const [rooms, setRooms] = useState([]);

  // const currentUser = fetchUser();

  useEffect(() => {
    // let result1 = [];
    // let result2 = [];

    // firebase
    //   .firestore()
    //   .collection("rooms")
    //   .where("userId1", "==", firebase.auth().currentUser.uid)
    //   .get()
    //   .then((snapshot) => {

    //   })

    const subscriber = firebase
      .firestore()
      .collection("rooms")
      .where(
        "userId1",
        "==",
        firebase.auth().currentUser.uid,
        OR,
        "userId2",
        "==",
        firebase.auth().currentUser.uid
      )
      .onSnapshot((querySnapshot) => {
        const rooms = [];

        querySnapshot.forEach((documentSnapshot) => {
          rooms.push({
            ...documentSnapshot.data(),
            key: documentSnapshot.id,
          });
        });
        setRooms(rooms);
        setLoading(false);

        for (let roomObject of rooms) {
          console.log(roomObject.users.idUser1);
        }
      });
    return () => {
      subscriber();
    };
  }, []);

  // useEffect(() => {
  //   const unsubscribe = firebase.firestore().collection('rooms').onSnapshot(querySnapshot =>{
  //     const rooms = querySnapshot.docs.map(documentSnapshot => {
  //       return{
  //         _id:documentSnapshot.id,
  //         // give defaults
  //         name: '',
  //         ...documentSnapshot.data()
  //       }
  //     })
  //     setRooms(rooms);
  //   })

  return (
    <Container>
      <FlatList
        data={rooms}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <Card
            onPress={() =>
              navigation.navigate("Chat", { userName: item.userName })
            }
          >
            <UserInfo>
              <UserImgWrapper>
                <UserImg source={item.userImg} />
              </UserImgWrapper>
              <TextSection>
                <UserInfoText>
                  <UserName>{item.userName}</UserName>
                  {/* <PostTime>{item.messageTime}</PostTime> */}
                </UserInfoText>
                <MessageText>{item.lastMessage}</MessageText>
              </TextSection>
            </UserInfo>
          </Card>
        )}
      />
    </Container>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
});
